<?php
	
	function generate_captcha($len=4)
	{
		global $C;
		$images	= array (
			'2'	=> '',
			'3'	=> '',
			'4'	=> '',
			'5'	=> '',
			'6'	=> '',
			'7'	=> '',
			'8'	=> '',
			'9'	=> '',
			'D'	=> '',
			'F'	=> '',
			'G'	=> '',
			'I'	=> '',
			'K'	=> '',
			'L'	=> '',
			'N'	=> '',
			'Q'	=> '',
			'R'	=> '',
			'S'	=> '',
			'W'	=> '',
			'Y'	=> '',
		);
		$len	= intval($len);
		$len	= max($len, 1);
		$len	= min($len, 10);
		$keys	= array_keys($images);
		$sel	= array();
		for($i=0; $i<$len; $i++) {
			$sel[]	= $keys[rand(0,count($keys)-1)];
		}
		$word	= '';
		$html	= '';
		foreach($sel as $key) {
			$filename	= 'captcha_'.md5(time().$key.rand(0,99999)).'.gif';
			$file		= $C->TMP_DIR.$filename;
			$fp	= fopen($file, 'w');
			fwrite($fp, base64_decode($images[$key]));
			fclose($fp);
			$html	.= '<img src="'.$C->TMP_URL.$filename.'" alt="" style="width:36px;height:41px;border:0px;margin:0px;padding:0px;" />';
			$word	.= $key;
		}
		$word	= strtolower($word);
		return array($word, $html);
	}
	
	function cleanup_captcha_files()
	{
		clearstatcache();
		global $C;
		$dir	= opendir($C->TMP_DIR);
		while($file = readdir($dir)) {
			if( $file == '.' || $file == '..' ) { continue; }
			if( preg_match('/^captcha_/i', $C->TMP_DIR.$file) ) { continue; }
			$time	= fileatime($C->TMP_DIR.$file);
			if( ! $time ) { continue; }
			if( $time < time()-10*60 ) { rm( $C->TMP_DIR.$file ); }
		}
		closedir($dir);
	}
	
?>